var GeoCoderFrontend,GeoCoder,gm,list;if('undefined'===jQuery||'undefined'===google){var google=null;var jQuery=null;}
if(null!==(jQuery&&google)){jQuery(document).ready(function($){GeoCoderFrontend.init();});}
(function($){GeoCoderFrontend={init:function(){gm=this;list=null;$('.geco_dynamic_map').each(function(){var elem=$(this);if(''!==gm.getLatlng(elem)){gm.geocodeLatlng(elem);}else{gm.geocodeAddress(elem);}});},showError:function(element,message){if(typeof message==='undefined'){message='No error message was given';}
$(element).html('<p>GoogleMaps failed with the following reason:<br><strong>'+message+'</strong></p>');},getID:function(element){var id=$(element).attr('ID');if(''!==id){return id;}else{gm.showError(element,'Element has no ID');}},getAddress:function(element){return $(element).attr('data-addr');},getLatlng:function(element){return element.attr('data-latlng');},getLatlngObject:function(element){var latlngStr=gm.getLatlng(element).split(",",2);return(latlngStr.length<2)?null:{lat:parseFloat(latlngStr[0]),lng:parseFloat(latlngStr[1])};},getMapOptions:function(element,latlng){if(typeof latlng!=='object'){gm.showError(element,'LatLng object is not type of object');}
var data=$(element);var zoom=parseInt(data.attr('data-zoom'),10);if(typeof zoom==='undefined'){zoom=16;}
var maptype=data.attr('data-maptype').toUpperCase();if(false===(maptype in google.maps.MapTypeId)){maptype='ROADMAP';}
return{zoom:zoom,center:new google.maps.LatLng(latlng.lat,latlng.lng),mapTypeId:google.maps.MapTypeId[maptype],panControl:true,zoomControl:true,mapTypeControl:true,scaleControl:true,streetViewControl:false,overviewMapControl:true};},addMarker:function(map,latlng,element){var markers;if(true===gm.isGeneralMap(element)){gm.ajaxMultiMarker();markers=$.parseJSON(gm.list);}else{markers={'A':latlng};markers.A.title='';markers.A.text='';markers.A.link='';}
gm.setMarkers(map,markers);},setMarkers:function(map,locations){var icon=$.parseJSON(GeoCoder.gmap_icon);var image=new google.maps.MarkerImage(icon.icon_url,new google.maps.Size(icon.icon_size.width,icon.icon_size.height),new google.maps.Point(icon.icon_origin.x,icon.icon_origin.y),new google.maps.Point(icon.icon_anchor.x,icon.icon_anchor.y));var shadow=new google.maps.MarkerImage(icon.shadow_url,new google.maps.Size(icon.shadow_size.width,icon.shadow_size.height),new google.maps.Point(icon.shadow_origin.x,icon.shadow_origin.y),new google.maps.Point(icon.shadow_anchor.x,icon.shadow_anchor.y));var shape={coord:[icon.icon_origin.x,icon.icon_origin.y,icon.icon_origin.x,icon.icon_size.height,icon.icon_size.width,icon.icon_size.height,icon.icon_size.width,icon.icon_origin.y],type:'poly'};for(var i in locations){if('undefinde'!==typeof locations[i]){var data=locations[i];var latlng=new google.maps.LatLng(data.lat,data.lng);var marker=new google.maps.Marker({position:latlng,map:map,shadow:shadow,icon:image,shape:shape,title:data.title});if(''!==data.title){var contentString='<div id="content">'+'<div id="siteNotice">'+'</div>'+'<h1 id="firstHeading" class="firstHeading">'+data.title+'</h1>'+'<div id="bodyContent">'+'<p>'+data.text+'</p>'+'<p><a href="'+data.link+'">'+GeoCoder.readmore+'</a>'+'</div>'+'</div>';var infowindow=new google.maps.InfoWindow({content:contentString});gm.addInfoWindow(map,marker,infowindow);}}}},addInfoWindow:function(map,marker,infowindow){google.maps.event.addListener(marker,'click',function(){infowindow.open(map,marker);});},isGeneralMap:function(element){var flag=$(element).attr('data-generalmap');return(1===parseInt(flag,10));},geocodeLatlng:function(element){var latlng=gm.getLatlngObject(element);if(null!==latlng){var map=new google.maps.Map(document.getElementById(gm.getID(element)),gm.getMapOptions(element,latlng));gm.addMarker(map,latlng,element);}else{gm.showError(element,'Map is not a general map and can not extract latitude/longitude from stored data');}},geocodeAddress:function(element){var address=gm.getAddress(element);if(''!==address){var geocoder=new google.maps.Geocoder();geocoder.geocode({'address':address},function(results,status){if(status===google.maps.GeocoderStatus.OK){var latlng={lat:results[0].geometry.location.Ya,lng:results[0].geometry.location.Za};var map=new google.maps.Map(document.getElementById(gm.getID(element)),gm.getMapOptions(element,latlng));gm.addMarker(map,latlng,element);}else{gm.showError(element,status);}});}},ajaxMultiMarker:function(){jQuery.ajax({type:'POST',url:GeoCoder.ajaxurl,data:{'action':'geco_multimarker','_ajax_nonce':GeoCoder.nonce,'async':false},success:function(result){gm.list=result;},async:false});}};})(jQuery);